#include <stdio.h>

int main() {
    int num = 1234;
    int rev = 0;

    while (num != 0) {
        int digit = num % 10;
        rev = rev * 10 + digit;
        num /= 10;
    }

    printf("Reverse = %d\n", rev);
    return 0;
}

#include <stdio.h>

int main() {
    int num = 121, original, rev = 0;
    original = num;

    while (num != 0) {
        int digit = num % 10;
        rev = rev * 10 + digit;
        num /= 10;
    }

    if (original == rev)
        printf("Palindrome Number\n");
    else
        printf("Not Palindrome\n");

    return 0;
}

#include <stdio.h>

int main() {
    int num = 29, i, isPrime = 1;

    if (num <= 1) isPrime = 0;

    for (i = 2; i <= num / 2; i++) {
        if (num % i == 0) {
            isPrime = 0;
            break;
        }
    }

    if (isPrime)
        printf("Prime Number\n");
    else
        printf("Not Prime Number\n");

    return 0;
}

