#include <stdio.h>

int main() {
    int arr[] = {5, 10, 15, 20, 25, 30};
    int n = 6;
    int key = 20;

    int low = 0, high = n - 1, mid;
    int found = -1;

    while (low <= high) {
        mid = (low + high) / 2;

        if (arr[mid] == key) {
            found = mid;
            break;
        } else if (arr[mid] < key) {
            low = mid + 1;
        } else {
            high = mid - 1;
        }
    }

    if (found != -1)
        printf("Found at index: %d\n", found);
    else
        printf("Not Found\n");

    return 0;
}

