#include <stdio.h>

int main() {
    char str[] = "madam";
    int len = 0;

    while (str[len] != '\0') {
        len++;
    }

    int start = 0;
    int end = len - 1;
    int isPalindrome = 1;

    while (start < end) {
        if (str[start] != str[end]) {
            isPalindrome = 0;
            break;
        }
        start++;
        end--;
    }

    if (isPalindrome)
        printf("Palindrome String\n");
    else
        printf("Not Palindrome\n");

    return 0;
}

