#include <math.h>

int main() {
    int num = 153;
    int original = num;
    int digits = 0;
    int temp = num;

    while (temp != 0) {
        digits++;
        temp /= 10;
    }

    int sum = 0;
    temp = num;

    while (temp != 0) {
        int digit = temp % 10;
        sum += pow(digit, digits);
        temp /= 10;
    }

    if (sum == original)
        printf("Armstrong Number\n");
    else
        printf("Not Armstrong\n");

    return 0;
}

#include <stdio.h>

int main() {
    int a = 12, b = 18;

    while (b != 0) {
        int temp = b;
        b = a % b;
        a = temp;
    }

    printf("GCD = %d\n", a);
    return 0;
}

#include <stdio.h>

int gcd(int a, int b) {
    while (b != 0) {
        int temp = b;
        b = a % b;
        a = temp;
    }
    return a;
}

int main() {
    int a = 12, b = 18;
    int g = gcd(a, b);
    int lcm = (a * b) / g;

    printf("LCM = %d\n", lcm);
    return 0;
}
