#include <stdio.h>

int main() {
    int a = 10;
    int *ptr = &a;

    printf("Value of a = %d\n", a);
    printf("Address of a = %p\n", (void*)&a);
    printf("Pointer ptr = %p\n", (void*)ptr);
    printf("Value using pointer *ptr = %d\n", *ptr);

    return 0;
}

#include <stdio.h>

void swap(int *x, int *y) {
    int temp = *x;
    *x = *y;
    *y = temp;
}

int main() {
    int a = 10, b = 20;

    printf("Before Swap: a=%d b=%d\n", a, b);
    swap(&a, &b);
    printf("After Swap: a=%d b=%d\n", a, b);

    return 0;
}

#include <stdio.h>
#include <string.h>

struct Student {
    int roll;
    char name[50];
    float marks;
};

int main() {
    struct Student s;

    s.roll = 1;
    strcpy(s.name, "Hariom");
    s.marks = 95.5;

    printf("Roll: %d\n", s.roll);
    printf("Name: %s\n", s.name);
    printf("Marks: %.2f\n", s.marks);

    return 0;
}

#include <stdio.h>

int main() {
    FILE *fp = fopen("data.txt", "w");

    if (fp == NULL) {
        printf("File cannot be opened\n");
        return 0;
    }

    fprintf(fp, "Hello File in C!\n");
    fclose(fp);

    printf("File written successfully\n");
    return 0;
}

#include <stdio.h>

int main() {
    FILE *fp = fopen("data.txt", "r");
    char line[100];

    if (fp == NULL) {
        printf("File cannot be opened\n");
        return 0;
    }

    printf("File Content:\n");
    while (fgets(line, sizeof(line), fp)) {
        printf("%s", line);
    }

    fclose(fp);
    return 0;
}
