public class SimpleCalculatorUsingSwitch {
    public static void main(String[] args) {
        int a = 15;
        int b = 3;
        char op = '*';

        switch (op) {
            case '+':
                System.out.println("Result = " + (a + b));
                break;
            case '-':
                System.out.println("Result = " + (a - b));
                break;
            case '*':
                System.out.println("Result = " + (a * b));
                break;
            case '/':
                System.out.println("Result = " + (a / b));
                break;
            default:
                System.out.println("Invalid Operator");
        }
    }
}

